// AI usage and security events timeline visualization
let aiUsage = SigninLogs
| where TimeGenerated > {TimeRange:start}
| where AppDisplayName has_any ("AI", "Copilot", "chat", "gpt")
| summarize AIUsage = count() by bin(TimeGenerated, 1h)
| extend EventType = "AI Usage";

let threats = SecurityAlert
| where TimeGenerated > {TimeRange:start}
| where AlertName has_any ("Jailbreak", "Prompt", "AI", "Model")
| summarize ThreatCount = count() by bin(TimeGenerated, 1h)
| extend EventType = "Security Threat";

// Combine data for the shared timeline view
aiUsage
| project TimeGenerated, Count = AIUsage, EventType
| union (
    threats
    | project TimeGenerated, Count = ThreatCount, EventType
)
| render timechart

// Correlation risk assessment summary - Real-time metrics from actual data
let totalThreats = toscalar(
    SecurityAlert
    | where TimeGenerated > {TimeRange:start}
    | where AlertName has_any ("Jailbreak", "Prompt", "AI", "Model")
    | summarize count()
);

let totalUsage = toscalar(
    SigninLogs
    | where TimeGenerated > {TimeRange:start}
    | where AppDisplayName has_any ("AI", "Copilot", "chat", "gpt")
    | summarize count()
);

let uniqueUsers = toscalar(
    SigninLogs
    | where TimeGenerated > {TimeRange:start}
    | where AppDisplayName has_any ("AI", "Copilot", "chat", "gpt")
    | summarize dcount(UserPrincipalName)
);

let correlatedEvents = toscalar(
    SigninLogs
    | where TimeGenerated > {TimeRange:start}
    | where AppDisplayName has_any ("AI", "Copilot", "chat", "gpt")
    | extend UsageHour = bin(TimeGenerated, 1h)
    | join kind=inner (
        SecurityAlert
        | where TimeGenerated > {TimeRange:start}
        | where AlertName has_any ("Jailbreak", "Prompt", "AI", "Model")
        | extend ThreatHour = bin(TimeGenerated, 1h)
    ) on $left.UsageHour == $right.ThreatHour
    | summarize dcount(UsageHour)
);

let riskRatio = case(totalUsage > 0, round(todouble(totalThreats) / todouble(totalUsage) * 100, 2), 0.0);

let riskLevel = case(
    riskRatio > 5, "🔥 High Risk",
    riskRatio > 2, "🟠 Moderate Risk",
    "🟢 Low Risk"
);

// Create table using union of print statements
print Metric = "📊 Total AI Usage", Value = tostring(totalUsage), Description = "Number of AI application sign-ins during the selected window"
| union (print Metric = "⚠️ Total Security Threats", Value = tostring(totalThreats), Description = "AI-related security alerts detected in the same window")
| union (print Metric = "👥 Active Users", Value = tostring(uniqueUsers), Description = "Distinct users interacting with AI applications")
| union (print Metric = "🔗 Correlated Time Windows", Value = tostring(correlatedEvents), Description = "Hourly periods where usage and threats overlapped")
| union (print Metric = "📈 Risk Ratio", Value = strcat(tostring(riskRatio), "%"), Description = "Percentage of threats compared to total AI usage")
| union (print Metric = "🎯 Overall Risk Level", Value = riskLevel, Description = "Risk posture derived from correlated statistics")

// AI Data Governance Compliance Score - Real-time metrics from actual data
let totalAILogins = toscalar(
    SigninLogs
    | where TimeGenerated > ago(30d)
    | where AppDisplayName has_any ("AI", "Copilot", "chat", "gpt")
    | summarize count()
);

let complianceLogins = toscalar(
    SigninLogs
    | where TimeGenerated > ago(30d)
    | where AppDisplayName has_any ("Security Copilot", "Azure AI Studio", "Microsoft 365 Copilot")
    | summarize count()
);

let violationLogins = toscalar(
    SigninLogs
    | where TimeGenerated > ago(30d)
    | where AppDisplayName has_any ("ChatGPT", "Claude", "Google Bard")
    | summarize count()
);

let dataLeakAlerts = toscalar(
    SecurityAlert
    | where TimeGenerated > ago(30d)
    | where AlertName has_any ("DLP", "Data", "Leak") and Description has_any ("AI", "Copilot")
    | summarize count()
);

let complianceRate = case(totalAILogins > 0, round(todouble(complianceLogins) / todouble(totalAILogins) * 100, 1), 0.0);
let violationRate = case(totalAILogins > 0, round(todouble(violationLogins) / todouble(totalAILogins) * 100, 1), 0.0);
let dataRiskScore = case(
    dataLeakAlerts >= 10, 90,
    dataLeakAlerts >= 5, 70,
    dataLeakAlerts >= 1, 50,
    20
);
let overallRisk = case(
    violationRate > 30 or dataRiskScore >= 70, "🔥 High Risk",
    violationRate > 15 or dataRiskScore >= 50, "🟠 Medium Risk", 
    violationRate > 5, "🟡 Needs Attention",
    "🟢 Low Risk"
);

// Create table using union of print statements
print Metric = "✅ AI Usage Compliance Rate", Value = strcat(tostring(complianceRate), "%"), Description = "Percentage of compliant AI application usage"
| union (print Metric = "⚠️ AI Violation Rate", Value = strcat(tostring(violationRate), "%"), Description = "Percentage of restricted AI application usage")
| union (print Metric = "📊 Data Risk Score", Value = tostring(dataRiskScore), Description = "Risk score based on data leak alerts")
| union (print Metric = "🎯 Overall Risk Level", Value = overallRisk, Description = "Comprehensive data governance risk assessment")
